import { StyleSheet, Text, View, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";

const DEFAULT_IMAGE =
  "https://notjustdev-dummy.s3.us-east-2.amazonaws.com/uber-eats/company1.jpeg";

const CompanyItem = ({ company }) => {
  const navigation = useNavigation();

  const onPress = () => {
    navigation.navigate("Company", { id: company.id });
  };

  return (
    <Pressable onPress={onPress} style={styles.companyContainer}>
      <Image
        source={{
          uri: company.image.startsWith("http")
            ? company.image
            : DEFAULT_IMAGE,
        }}
        style={styles.image}
      />
      <View style={styles.row}>
        <View>
          <Text style={styles.title}>{company.name}</Text>
          <Text style={styles.subtitle}>
            $ {company.deliveryFee.toFixed(1)} &#8226;{" "}
            {company.minDeliveryTime}-{company.maxDeliveryTime} minutes
          </Text>
        </View>

        <View style={styles.rating}>
          <Text>{company.rating.toFixed(1)}</Text>
        </View>
      </View>
    </Pressable>
  );
};

export default CompanyItem;

const styles = StyleSheet.create({
  companyContainer: {
    width: "100%",
    marginVertical: 10,
  },
  image: {
    width: "100%",
    aspectRatio: 5 / 3,
    marginBottom: 5,
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
    marginVertical: 5,
  },
  subtitle: {
    color: "grey",
  },
  row: {
    flexDirection: "row",
    alignItems: "center",
  },
  rating: {
    marginLeft: "auto",
    backgroundColor: "lightgray",
    width: 30,
    height: 30,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 20,
  },
});
